using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ProjectEuler
{
    public class Problem8
    {
        string nums = "";
        int result = 0;
        string resultString = "";
        public Problem8()
        {
            int x = 0;
            int sum = 1;
            int length = nums.Length;
            int numsInSum = 0;
            while (x < length)
            {
                //int remove = int.Parse(nums[x - 5].ToString());
                int add = int.Parse(nums[x].ToString());
                if (add == 0)
                {
                    sum = 1;
                    numsInSum = 0;
                }
                else
                {
                    if (numsInSum>=5) sum /= int.Parse(nums[x-5].ToString());
                    if (add != 0) sum *= add;
                    if (sum > result)
                    {
                        result = sum;
                        if(numsInSum>=5)resultString = nums.Substring(x-5, 5);
                    }
                    numsInSum++;
                }
                x++;
            }

        }
        public override string ToString()
        {
            return result + " - " + resultString;
        }

    }
}
